import React, { useEffect, useState } from 'react';
import Header from "../Header/Header";
import axios from 'axios';
import styles from './style.module.css'; // Use correct CSS module naming

const Favorites = () => {
    const [favoriteRecipes, setFavoriteRecipes] = useState([]);
    const [categories, setCategories] = useState([]);
    const [user] = useState(JSON.parse(localStorage.getItem("user") || "{}"));
    const [loading, setLoading] = useState(true); // State to handle loading

    useEffect(() => {
        const fetchFavorites = async () => {
            if (user && user._id) {
                try {
                    const response = await axios.get(`http://localhost:5000/api/favorites/${user._id}`);
                    setFavoriteRecipes(response.data);
                } catch (error) {
                    console.error("Error fetching favorite recipes", error);
                } finally {
                    setLoading(false); // Set loading to false after fetching
                }
            } else {
                console.log("User not logged in.");
                setLoading(false); // Set loading to false if not logged in
            }
        };

        const fetchCategories = async () => {
            try {
                const response = await axios.get('https://www.themealdb.com/api/json/v1/1/categories.php');
                setCategories(response.data.categories);
            } catch (error) {
                console.error("Error fetching categories", error);
            }
        };

        fetchFavorites();
        fetchCategories();
    }, [user]);

    const removeFavorite = async (recipeId) => {
        const updatedFavorites = favoriteRecipes.filter(recipe => recipe.recipeId !== recipeId);
        setFavoriteRecipes(updatedFavorites);
        localStorage.setItem('favoriteRecipes', JSON.stringify(updatedFavorites));

        try {
            await axios.delete(`http://localhost:5000/api/favorites`, { data: { userId: user._id, recipeId } });
        } catch (error) {
            console.error("Error removing favorite recipe", error);
        }
    };

    return (
        <>
            <Header />
            <div className={styles.container}>
                <h1>Your Favorite Recipes</h1>
                {loading ? ( // Show loading state
                    <p>Loading...</p>
                ) : favoriteRecipes.length > 0 ? (
                    <ul className={styles.recipeList}>
                        {favoriteRecipes.map(recipe => (
                            <li key={recipe.recipeId} className={styles.recipeItem}>
                                <img
                                    src={recipe.strMealThumb}
                                    alt={recipe.recipeName}
                                    className={styles.recipeImage} // Assuming styles are defined
                                />
                                <h2>{recipe.recipeName}</h2> {/* Ensure this is the correct key */}
                                <button onClick={() => removeFavorite(recipe.recipeId)}>Remove</button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No favorite recipes found.</p>
                )}
            </div>
        </>
    );
};

export default Favorites;
