import React, { useEffect, useState } from 'react';
import Header from "../Header/Header";
import axios from 'axios'; // Make sure axios is imported

const Favorites = () => {
    const [favoriteRecipes, setFavoriteRecipes] = useState([]);
    const userId = localStorage.getItem('userId'); // Assuming user ID is stored in localStorage

    useEffect(() => {
        const fetchFavorites = async () => {
            if (userId) {
                try {
                    const response = await axios.get(`http://localhost:5000/api/favorites/${userId}`);
                    setFavoriteRecipes(response.data);
                } catch (error) {
                    console.error("Error fetching favorite recipes", error);
                }
            } else {
                console.log("User not logged in.");
            }
        };

        fetchFavorites();
    }, [userId]);

    const removeFavorite = async (recipeId) => {
        const updatedFavorites = favoriteRecipes.filter(recipe => recipe.recipeId !== recipeId);
        setFavoriteRecipes(updatedFavorites);
        localStorage.setItem('favoriteRecipes', JSON.stringify(updatedFavorites));

        // Optionally remove from the backend as well
        try {
            await axios.delete(`http://localhost:5000/api/favorites`, { data: { userId, recipeId } });
        } catch (error) {
            console.error("Error removing favorite recipe", error);
        }
    };

    return (
        <>
            <Header />
            <div>
                <h1>Your Favorite Recipes</h1>
                {favoriteRecipes.length > 0 ? (
                    <ul>
                        {favoriteRecipes.map(recipe => (
                            <li key={recipe.recipeId}>
                                {recipe.recipeName} {/* Adjust this according to your actual response structure */}
                                <button onClick={() => removeFavorite(recipe.recipeId)}>Remove</button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No favorite recipes found.</p>
                )}
            </div>
        </>
    );
};

export default Favorites;
