import { useState, useEffect } from "react";
import axios from "axios";
import styles from "./style.modules.css"; // Ensure this file exists
import Header from "../Header/Header"; // Adjust path if necessary

const Favorites = () => {
    const [favorites, setFavorites] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [user] = useState(JSON.parse(localStorage.getItem("user") || "{}"));

    useEffect(() => {
        const fetchFavorites = async () => {
            if (!user._id) return; // No user logged in

            try {
                const res = await axios.get(`http://localhost:5000/api/favorites/${user._id}`);
                console.log("Favorites fetched:", res.data); // Log the fetched data
                setFavorites(res.data);
            } catch (err) {
                console.error("Error fetching favorites:", err);
                setError("Could not load favorites.");
            } finally {
                setLoading(false);
            }
        };

        fetchFavorites();
    }, [user]);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <>
            <Header />
            <div className={styles.favorites_container}>
                <h1>Your Favorite Recipes</h1>
                <div className={styles.recipe_grid}>
                    {favorites.length === 0 ? (
                        <p>No favorites added yet.</p>
                    ) : (
                        favorites.map((recipe) => (
                            <div key={recipe.idMeal} className={styles.recipe_card}>
                                <img
                                    src={recipe.strMealThumb} // Now this should have the correct URL
                                    alt={recipe.strMeal} // Adjust to use actual recipe name
                                    className={styles.recipe_img}
                                />
                                <h3>{recipe.strMeal}</h3> // Now this should correctly display the recipe name
                            </div>
                        ))
                    )}
                </div>
            </div>
        </>
    );
};

export default Favorites;
