import { useState, useEffect } from "react";
import axios from "axios";
import styles from "./styles.module.css";
import Header from "../Header/Header";

const Main = () => {
	const [categories, setCategories] = useState([]);
	const [selectedCategory, setSelectedCategory] = useState("pork");
	const [recipes, setRecipes] = useState([]);
	const [favorites, setFavorites] = useState([]);
	const [user] = useState(JSON.parse(localStorage.getItem("user") || "{}"));

	// Fetch categories when the component mounts
	useEffect(() => {
		const fetchCategories = async () => {
			try {
				const res = await axios.get(
					"https://www.themealdb.com/api/json/v1/1/categories.php"
				);
				setCategories(res.data.categories);
			} catch (error) {
				console.error("Error fetching categories:", error);
			}
		};

		fetchCategories();
	}, []);

	// Fetch recipes based on the selected category
	useEffect(() => {
		const fetchRecipes = async () => {
			try {
				const res = await axios.get(
					`https://www.themealdb.com/api/json/v1/1/filter.php?c=${selectedCategory}`
				);
				setRecipes(res.data.meals);
			} catch (error) {
				console.error("Error fetching recipes:", error);
			}
		};

		if (selectedCategory) fetchRecipes();
	}, [selectedCategory]);

	// Function to add a recipe to favorites
	const handleAddToFavorites = async (recipe) => {


		if (!user._id) {
			alert("Please log in to add favorites.");
			return;
		}

		try {
			await axios.post("http://localhost:5000/api/favorites", {
				userId: user._id,
				recipeId: recipe.idMeal, // The ID of the recipe being added to favorites
			});

			// Update local state and local storage for favorites
			const updatedFavorites = [...favorites, recipe];
			setFavorites(updatedFavorites);
			localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
		} catch (error) {
			console.error("Error adding to favorites:", error);
		}
	};

	return (
		<>
			<Header />

			<div className={styles.main_container}>
				<nav className={styles.navbar}>
					<h1>Recipe App</h1>
				</nav>

				<div className={styles.category_buttons}>
					{categories.map((category) => (
						<button
							key={category.idCategory}
							className={
								selectedCategory === category.strCategory
									? styles.active_category
									: styles.category_btn
							}
							onClick={() => setSelectedCategory(category.strCategory)}
						>
							{category.strCategory}
						</button>
					))}
				</div>

				<div className={styles.recipe_grid}>
					{recipes.map((recipe) => (
						<div key={recipe.idMeal} className={styles.recipe_card}>
							<img
								src={recipe.strMealThumb}
								alt={recipe.strMeal}
								className={styles.recipe_img}
							/>
							<h3>{recipe.strMeal}</h3>
							<button onClick={() => handleAddToFavorites(recipe)}>
								Add to Favorites
							</button>
						</div>
					))}
				</div>
			</div>
		</>
	);
};

export default Main;
