import { Link, useNavigate } from "react-router-dom"; // Import useNavigate
import styles from "./styles.module.css";
import logo from "../Images/logo.png";
import {useState} from "react"; // Make sure this path is correct

const Header = () => {
    const navigate = useNavigate(); // Initialize useNavigate
    const [user] = useState(JSON.parse(localStorage.getItem("user") || "{}"));

    const handleLogout = () => {
        localStorage.removeItem("token");
        navigate("/"); // Redirect to home page after logout
    };

    return (
        <header className={styles.header}>
            {/* Left Side - Logo */}
            <div className={styles.logo_container}>
                <img src={logo} alt="Logo" className={styles.logo} />
            </div>

            {/* Middle - Navigation Buttons */}
            <nav className={styles.nav_buttons}>
                <a href="/" className={styles.nav_link}>
                    Home
                </a>


                <Link to="/favorites" className={styles.nav_link}>
                    Favorites {/* Changed from "Favourite" to "Favorites" */}
                </Link>
            </nav>

            {/* Right Side - Login/Logout Button */}
            <div className={styles.right_buttons}>
                {localStorage.getItem("token") ? (
                    <button className={styles.white_btn} onClick={handleLogout}>
                        Logout
                    </button>
                ) : (
                    <Link to="/login">
                        <button className={styles.white_btn}>Login</button>
                    </Link>
                )}
            </div>
        </header>
    );
};

export default Header;
