import { useState, useEffect } from "react";
import axios from "axios";
import styles from "./style.modules.css";
import Header from "../Header/Header";

const Favorites = () => {
    const [favorites, setFavorites] = useState([]);
    const [user] = useState(JSON.parse(localStorage.getItem("user") || "{}"));

    useEffect(() => {
        const fetchFavorites = async () => {
            if (!user._id) return;

            try {
                const res = await axios.get(`http://localhost:5000/api/Favorites/${user._id}`);
                setFavorites(res.data); // Assume this returns an array of favorite recipes
            } catch (error) {
                console.error("Error fetching favorites:", error);
            }
        };

        fetchFavorites();
    }, [user]);

    return (
        <>
            <Header />
            <div className={styles.favorites_container}>
                <h1>Your Favorite Recipes</h1>
                <div className={styles.recipe_grid}>
                    {favorites.map((recipe) => (
                        <div key={recipe.idMeal} className={styles.recipe_card}>
                            <img
                                src={recipe.strMealThumb}
                                alt={recipe.strMeal}
                                className={styles.recipe_img}
                            />
                            <h3>{recipe.strMeal}</h3>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
};

export default Favorites;
