import { useState, useEffect } from "react";
import axios from "axios";
import styles from "./styles.module.css";
import Header from "../Header/Header";

const Main = () => {
	const [categories, setCategories] = useState([]);
	const [selectedCategory, setSelectedCategory] = useState("pork");
	const [recipes, setRecipes] = useState([]);
	const [favorites, setFavorites] = useState([]);

	useEffect(() => {
		// Fetch categories when the component mounts, limited to 5
		const fetchCategories = async () => {
			const res = await axios.get(
				"https://www.themealdb.com/api/json/v1/1/categories.php"
			);
			setCategories(res.data.categories.slice(0, 5)); // Get only the first 5 categories
		};

		fetchCategories();
	}, []);

	useEffect(() => {
		// Fetch recipes based on the selected category
		const fetchRecipes = async () => {
			const res = await axios.get(
				`https://www.themealdb.com/api/json/v1/1/filter.php?c=${selectedCategory}`
			);
			setRecipes(res.data.meals);
		};

		if (selectedCategory) fetchRecipes();
	}, [selectedCategory]);

	const handleLogout = () => {
		localStorage.removeItem("token");
		window.location.reload();
	};

	const handleAddToFavorites = (recipe) => {
		const updatedFavorites = [...favorites, recipe];
		setFavorites(updatedFavorites);
		localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
	};

	return (
		<>
			<Header />

			<div className={styles.main_container}>
				<nav className={styles.navbar}>
					<h1>Recipe App</h1>
				</nav>

				<div className={styles.category_buttons}>
					{categories.map((category) => (
						<button
							key={category.idCategory}
							className={
								selectedCategory === category.strCategory
									? styles.active_category
									: styles.category_btn
							}
							onClick={() => setSelectedCategory(category.strCategory)}
						>
							{category.strCategory}
						</button>
					))}
				</div>

				<div className={styles.recipe_grid}>
					{recipes.map((recipe) => (
						<div key={recipe.idMeal} className={styles.recipe_card}>
							<img
								src={recipe.strMealThumb}
								alt={recipe.strMeal}
								className={styles.recipe_img}
							/>
							<h3>{recipe.strMeal}</h3>
							<button
								className={styles.favorite_btn}
								onClick={() => handleAddToFavorites(recipe)}
							>
								<span className={styles.heart_icon}>❤️</span>
							</button>
						</div>
					))}
				</div>
			</div>
		</>
	);
};

export default Main;
